using System;
using System.Numerics;
using Algorithms.Numeric;
using NUnit.Framework;

namespace Algorithms.Tests.Numeric
{
    public static class FactorialTests
    {
        [Test]
        [TestCase(5, 120)]
        [TestCase(1, 1)]
        [TestCase(0, 1)]
        [TestCase(4, 24)]
        [TestCase(18, 6402373705728000)]
        [TestCase(10, 3628800)]
        public static void GetsFactorial(int input, long expected)
        {
            // Arrange

            // Act
            var result = Factorial.Calculate(input);

            // Assert
            Assert.AreEqual(expected, result);
        }

        [Test]
        public static void GetsFactorialExceptionForNonPositiveNumbers(
            [Random(-1000, -1, 10, Distinct = true)]
            int input)
        {
            // Arrange

            // Act
            void Act() => Factorial.Calculate(input);

            // Assert

            _ = Assert.Throws<ArgumentException>(Act);
        }

        [TestCase(0, "1")]
        [TestCase(1, "1")]
        [TestCase(18L, "6402373705728000")]
        [TestCase(25L, "15511210043330985984000000")]
        [TestCase(50, "30414093201713378043612608166064768844377641568960512000000000000")]
        [TestCase(70, "11978571669969891796072783721689098736458938142546425857555362864628009582789845319680000000000000000")]
        [TestCase(100, "93326215443944152681699238856266700490715968264381621468592963895217599993229915608941463976156518286253697920827223758251185210916864000000000000000000000000")]
        [TestCase(500, "")]
        [TestCase(1000, "")]
        public static void GetsFactorial<T>(T input, string expectedResult) where T : notnull, IBinaryInteger<T>
        {
            // Arrange
            BigInteger expectedResultBigInt = BigInteger.Parse(expectedResult);

            // Act
            BigInteger result = Factorial.Calculate(input);

            // Assert
            Assert.AreEqual(expectedResultBigInt, result);
        }

        [TestCase(-1)]
        [TestCase(-5)]
        [TestCase(-50L)]
        [TestCase(-100L)]
        public static void GetsFactorialExceptionForNegativeNumbers<T>(T input) where T : notnull, IBinaryInteger<T>
        {
            // Arrange

            // Act
            void Act() => Factorial.Calculate(input);

            // Assert

            _ = Assert.Throws<ArgumentException>(Act);
        }
    }
}
